<html><head><title>Untitled Document</title><meta http-equiv="Content-Type" content="text/html; charset=ISO-8859-1"><link href="style1.css" rel="stylesheet" type="text/css"><style type="text/css"><!--body {	background-image: url(images/bg.gif);	margin: 0;	margin-top: 15px;	font-family: Arial, Helvetica, sans-serif;	font-size: 12px;	color: #000;}--></style></head><body><?php	$username = $_POST['username'];	#assign value	$useraddr = $_POST['useraddr'];	#assign value	$userphone = $_POST['userphone']; #assign value	$subject = $_POST['subject'];	#assign value	$comments = $_POST['comments'];	#assignvalue		if (!$useraddr and !$userphone) {		echo ("<span class=\"field\">ERROR:</span> No contact information was provided. Please include either your e-mail address or phone number, that way we can contact you as soon as possible.");	}	else {		$to = "tidalyarns@comcast.net";	#recipient		$re = $subject;	#subject		$msg = "From: $username\nPhone: $userphone\nEmail: $useraddr\n\n$comments";	#message		$headers .= "From: $useraddr \r\n";	#from		mail($to,$re,$msg,$headers);				echo ("Thank you for your interest in Tidal Yarns!<br><br><span class=\"field\">Message received from</span> <b>");		echo ($username);		echo ("</b><br><span class=\"field\">Reply to</span> <b>");		echo ($useraddr);		echo ("</b>");	}?></body></html>